mapper.update:
update huadiao_note_star
set `status` = 0
where `uid` = #{uid}
  and `note_id` = #{noteId}
  and `author_uid` = #{authorUid};

update huadiao_note_unlike
set `status` = 0
where `note_id` = #{noteId}
  and `uid` = #{uid}
  and `author_uid` = #{authorUid};

update huadiao_note_view
set `status` = 0
where `uid` = #{uid}
  and `author_uid` = #{authorUid}
  and `note_id` = #{noteId};

update huadiao_note_like
set `status` = 0
where `uid` = #{uid}
  and `note_id` = #{noteId}
  and `author_uid` = #{authorUid};

update huadiao_note_comment_like
set `status` = 0
where `note_id` = #{noteId}
  and `uid` = #{uid}
  and `author_uid` = #{authorUid}
  and `root_comment_id` = #{rootCommentId}
  and `sub_comment_id` = #{subCommentId};

update huadiao_note_comment_unlike
set `status` = 0
where `note_id` = #{noteId}
  and `uid` = #{uid}
  and `author_uid` = #{authorUid}
  and `root_comment_id` = #{rootCommentId}
  and `sub_comment_id` = #{subCommentId};

update huadiao_note_comment
set `status`      = 0,
    `delete_uid`  = #{deleteUid},
    `delete_time` = now()
where (`uid` = #{deleteUid} or `author_uid` = #{deleteUid})
  and `root_comment_id` = #{rootCommentId}

      and `sub_comment_id` = #{subCommentId}
  ;

mapper.select:
select count(*)
from huadiao_note_star
where `uid` = #{uid}
  and `status`;

select count(*)
from huadiao_note_view
where `author_uid` = #{uid}
  and `status`
  and `note_id` = #{noteId};

select 1
from huadiao_note_comment
where `note_id` = #{noteId}
  and `author_uid` = #{authorUid}
  and `root_comment_id` = #{rootCommentId}
  and (`sub_comment_id` = #{subCommentId} or (`sub_comment_id` = 0 and #{subCommentId} = 0))
  and `status`;
